// Arduino LED Blink Example
int ledPin = 13;
int delayTime = 1000;

void setup() {
  pinMode(ledPin, OUTPUT);
  Serial.begin(9600);
  Serial.println("LED Blink Started");
}

void loop() {
  digitalWrite(ledPin, HIGH);
  Serial.println("LED ON");
  delay(delayTime);

  digitalWrite(ledPin, LOW);
  Serial.println("LED OFF");
  delay(delayTime);
}
