// Arduino Servo Control
#include <Servo.h>

Servo myServo;
int servoPin = 9;
int angle = 0;
int step = 5;

void setup() {
  myServo.attach(servoPin);
  Serial.begin(9600);
  Serial.println("Servo Control Started");
  myServo.write(0);
}

void loop() {
  angle += step;

  if (angle >= 180 || angle <= 0) {
    step = -step;
  }

  myServo.write(angle);
  Serial.print("Angle: ");
  Serial.println(angle);
  delay(50);
}
