const PIR = {
  detect: () => {
    return Math.random() > 0.7;
  }
};

let motionDetected = false;
let lastDetection = null;

function checkMotion() {
  const detected = PIR.detect();

  if (detected && !motionDetected) {
    motionDetected = true;
    lastDetection = Date.now();
    console.log('Motion detected!');
    triggerAlert();
  } else if (!detected && motionDetected) {
    motionDetected = false;
    const duration = Date.now() - lastDetection;
    console.log(`Motion ended after ${duration}ms`);
  }

  return motionDetected;
}

function triggerAlert() {
  console.log('ALERT: Motion detected in area');
  return { alert: true, timestamp: Date.now() };
}

// Check motion every second
setInterval(checkMotion, 1000);
