const LED = {
  brightness: 0,
  state: false,

  setBrightness: function(value) {
    this.brightness = Math.max(0, Math.min(100, value));
    this.state = this.brightness > 0;
    console.log(`LED brightness: ${this.brightness}%`);
  },

  turnOn: function() {
    this.state = true;
    this.brightness = 100;
    console.log('LED turned ON');
  },

  turnOff: function() {
    this.state = false;
    this.brightness = 0;
    console.log('LED turned OFF');
  }
};

function autoBrightness(ambientLight) {
  if (ambientLight < 30) {
    LED.setBrightness(100);
  } else if (ambientLight < 60) {
    LED.setBrightness(50);
  } else {
    LED.setBrightness(0);
  }
}

// Simulate ambient light sensor
setInterval(() => {
  const ambientLight = Math.random() * 100;
  autoBrightness(ambientLight);
}, 2000);
