const DHT22 = {
  read: () => {
    return {
      temperature: 22.5 + Math.random() * 5,
      humidity: 45 + Math.random() * 10,
      timestamp: Date.now()
    };
  }
};

const readings = [];
const threshold = 25;

function monitorTemperature() {
  const data = DHT22.read();
  readings.push(data);

  if (data.temperature > threshold) {
    console.log(`Alert: Temperature ${data.temperature.toFixed(1)}°C exceeds threshold`);
    return { alert: true, temperature: data.temperature };
  }

  return { alert: false, temperature: data.temperature };
}

// Monitor every 5 seconds
setInterval(() => {
  const result = monitorTemperature();
  console.log(`Temperature: ${result.temperature.toFixed(1)}°C`);
}, 5000);
